import { defineComponent, reactive } from "vue";
import { Swiper, SwiperSlide } from "swiper/vue";
import SwiperCore, { Autoplay, Pagination } from "swiper"; // 引入样式

import "swiper/swiper.scss";
import "swiper/components/pagination/pagination.scss"; // 官网就是这么写的

export default {
  __name: 'swiperCom',
  props: {
    data: Array
  },

  setup(__props, {
    expose
  }) {
    expose();
    const props = __props;
    SwiperCore.use([Autoplay, Pagination]); // 引入两个组件

    defineComponent({
      components: {
        Swiper,
        SwiperSlide
      }
    }); // 接受来自父组件的值
    // 轮播图的相关配置

    let swiper_options = reactive({
      autoplay: {
        delay: 3000,
        disableOnInteraction: false
      },
      loop: true,
      speed: 500,
      pagination: {
        clickable: true
      }
    });
    const __returned__ = {
      props,
      swiper_options,
      defineComponent,
      reactive,
      Swiper,
      SwiperSlide,
      SwiperCore,
      Autoplay,
      Pagination
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

};
